import React, { useEffect, useState } from 'react'
import "./Product.css"
import Cart from './Cart';


const Product = () => {
    const [results, setResults] = useState([]);
    const [data, setData] = useState(
        {
            title: "",
            first: "",
            last: "",

        })

    useEffect(() => {
        const getUser = async () => {
            const url = 'https://randomuser.me/api/?inc=gender,name,nat,location,picture,email&results=20';
            const response = await fetch(url);
            // console.log(response);
            const resJson = await response.json();
            console.log(resJson.results);
            setResults(resJson.results);
            data.title = resJson.results[0].name.title
        }
        getUser();

    }, []);
    // console.log(results, "jii");

    const clickButton = (event) => {
        <Cart props="jj" />
        console.log(event)
    }

    return (
        <>
            <div className=''>
                <div className="cart1">
                    <div className="image">
                        <img src="https://www.google.com/url?sa=i&url=https%3A%2F%2Fwww.britannica.com%2Flist%2F10-women-scientists-who-should-be-famous-or-more-famous&psig=AOvVaw2eZbRa4Yvbu_nJu7eN5MWw&ust=1686474631867000&source=images&cd=vfe&ved=0CBEQjRxqFwoTCMjU46jBuP8CFQAAAAAdAAAAABAZ" class='rounded' width='155' alt="scientist" />
                    </div>
                    <div className="about">
                        <h1 className='Name'>Ms. Teatske Nijenhuis</h1>
                        <p><span className="span1">775</span>,Heldertwerg Bom,Zeeland,<span className="span2">Netherlands</span>, 18818</p>
                        <p >+6:00 - Almaty,<span className="span3">Dhaka</span>, Colombo</p>
                        <p><span className="span4">Female</span></p>
                    </div>
                </div>


                <div className="container-fluid mt-5">
                    <div className="row " onClick={clickButton}>

                        {
                            results?.map((currElement) => (

                                <div className="col-10 col-md-3 mt-5">
                                    <div className="card p-2">
                                        <div className="d-flex">
                                            <div className="ml-4 w-100">
                                                <p className='p-2 mt-2  d-flex  '><span className="span5">{currElement.gender}</span>. {currElement.nat}</p>
                                                <h3 >{currElement.name?.title} {currElement.name?.first} {currElement.name?.last}</h3>
                                                <p >{currElement.email}</p>

                                            </div>
                                        </div>
                                    </div>

                                </div>

                            )
                            )

                        }


                    </div>
                </div>
            </div>

        </>
    )
}

export default Product
